mapper.select:
select * form score
where
	sc_st_key = #{st_key}
and sc_su_key = #{su_key}

mapper.delete:
delete from score
where
	sc_st_key = #{st_key}
and sc_su_key = #{su_key}

mapper.update:
update score
set
	sc_midTerm = #{score.sc_midTerm}, 
	sc_finalTerm = #{score.sc_finalTerm}, 
	sc_perfomance = #{score.sc_perfomance}
where
	sc_st_key = #{score.sc_st_key}
and	sc_su_key = #{score.sc_su_key}
